const AvailableMeals = () => {
  useEffect(()=>{
    const fetchMeals=async()=>{
     const response=await fetch('https://react-http-63859-default-rtdb.firebaseio.com/meals.json');
     const responseData=await response.json();
     const loadedMeals=[];
     for(const key in responseData){
      loadedMeals.push({
        id:key,
        name:responseData[key].name,
        description:responseData[key].description,
        price
      })
     }
    };
    fetchMeals();

  },[])
  const mealsList = DUMMY_MEALS.map((meal) => (<MealItem key={meal.id} id={meal.id} name={meal.name} description={meal.description} price={meal.price} />));
  return <section className={classes.meals}>
    <Card>
      <ul>
        {mealsList}
      </ul>
    </Card>
  </section>
}
export default AvailableMeals
