import classes from './AvailabelMeals.module.css';
import { useEffect, useState } from 'react';
import Card from '../UI/Card.js';
import MealItem from './MealItem/MealItem.js'


const AvailableMeals = () => {
  const [meals, setMeals] = useState([])
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState();
  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch('https://react-http-63859-default-rtdb.firebaseio.com/meals.json');
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }
      const responseData = await response.json();
      const loadedMeals = [];
      for (const key in responseData) {
        loadedMeals.push({
          id: key,
          name: responseData[key].name,
          description: responseData[key].description,
          price: responseData[key].price,
        })
      }
      setIsLoading(false)
      setMeals(loadedMeals)
    };
    fetch

  }, [])
  if (isLoading) {
    return (
      <section className={classes.MealsLoading}>
        <p>Loading...</p>
      </section>
    )
  }
  if(httpError){
    <section className={classes.MealsError}>
      <p>{httpError}</p>
    </section>
  }
  const mealsList = meals.map((meal) => (<MealItem key={meal.id} id={meal.id} name={meal.name} description={meal.description} price={meal.price} />));
  return <section className={classes.meals}>
    <Card>
      <ul>
        {mealsList}
      </ul>
    </Card>
  </section>
}
export default AvailableMeals
